import React from 'react';

//the "like" button
//using hooks
import "./index.css"
import {useState, useEffect} from 'react'

let App =  function App() {
    const [isLiked, setIsliked] = userState(false)

    useEffect(()=>{
        //without an argument will run every time state is updated
        console.log(isLiked);
        //piece in array, is dependency---- what?
    }, [isLiked])
    const handleClick = () => {
        setIsliked(!isLiked)
    }

    return (
        <div className="App">

        </div>
    )
}
let Beers  = function Beers(props) {
    const {clickToLike, index} = props
    return (
        //implicit return 
        <ul className="beerbox">
            {props.beers.map((beer, index)=> (
                <li className="beerbottle">
                    <div>
                        <img src={beer.image_url} alt={beer.name}/>
                    </div>
                    <div>
                        <p>{beer.name}</p>
                        <p>IBU: {beer.ibu}</p>
                        <button  onClick={() => {clickToLike(index)}}>Like</button>
                    </div>

                </li>
            ))}
        </ul>
    )
}

export default {
    Beers,
    App
}
