import React from 'react';

let Beers  = function Beers(props) {
    return (
        //implicit return 
        <ul className="beerbox">
            {props.beers.map((beer)=> (
                <li className="beerbottle">
                    <div></div>
                    <img src={beer.image_url} alt={beer.name}/>
                    <p>{beer.name}</p>
                    <p>IBU: {beer.ibu}</p>
                </li>
            ))}
        </ul>
    )
}

export default Beers
