let Beers  = function Beers(props) {
    const {clickToLike, index} = props
    return (
        //implicit return 
        <ul className="beerbox">
            {props.beers.map((beer, index)=> (
                <li className="beerbottle">
                    <div>
                        <img src={beer.image_url} alt={beer.name}/>
                    </div>
                    <div>
                        <p>{beer.name}</p>
                        <p>IBU: {beer.ibu}</p>
                        <button  onClick={() => {clickToLike(index)}}>Like</button>
                    </div>

                </li>
            ))}
        </ul>
    )
}

export default {
    Beers
    // App
}
